package week1day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// n! = n(n-1)(n-2).....1
		int number = 4;
	    int i, fact= 1;
		
		for ( i= 1; i<= number;i++)
		{	fact = fact * i;
		}
		System.out.println("Factorial of "+number+"  is" +   fact);
	}}
		
		
				
				
			
	

		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
			
			

